function Account_js() {
    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
    };
    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }
    /*
     * Nonvisual components
     */
    Apperyio.mappings = Apperyio.mappings || {};
    Apperyio.mappings["Account_restservice1_onsuccess_mapping_0"] = {
        "homeScreen": "Account",
        "directions": []
    };
    Apperyio.mappings["Account_restservice1_onbeforesend_mapping_0"] = {
        "homeScreen": "Account",
        "directions": [
            {
                "from_name": "Account",
                "from_type": "UI",
                "to_name": "restservice1",
                "to_type": "SERVICE_REQUEST",
                "to_default": {
                    "headers": {
                        "X-Appery-Database-Id": "{database_id}",
                        "Content-Type": "application/json"
                    },
                    "parameters": {},
                    "body": {
                        "acl": {
                            "*": {
                                "write": true,
                                "read": true
                            }
                        }
                    }
                },
                "mappings": [
                    {
                        "source": "$['Name:text']",
                        "target": "$['body']['Name']"
                    },
                    {
                        "source": "$['Badge:text']",
                        "target": "$['body']['Badge_Num']"
                    }
                ]
            }
        ]
    };
    Apperyio.datasources = Apperyio.datasources || {};
    window.restservice1 = Apperyio.datasources.restservice1 = new Apperyio.DataSource(Magnetic_Report_create_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["Account_restservice1_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["Account_restservice1_onsuccess_mapping_0"]);
            alert('Your report has been submitted');
        },
        "onError": function(jqXHR, textStatus, errorThrown) {}
    });
    Apperyio.CurrentScreen = 'Account';
    _.chain(Apperyio.mappings)
        .filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        })
        .each(Apperyio.UIHandler.hideTemplateComponents);
    /*
     * Events and handlers
     */
    // On Load
    var Account_onLoad = function() {
        Account_elementsExtraJS();
        Account_deviceEvents();
        Account_windowEvents();
        Account_elementsEvents();
    };
    // screen window events
    function Account_windowEvents() {
        $('#Account').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
    };
    // device events
    function Account_deviceEvents() {
        document.addEventListener("deviceready", function() {
        });
    };
    // screen elements extra js
    function Account_elementsExtraJS() {
        // screen (Account) extra code
    };
    // screen elements handler
    function Account_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });
        $(document).off("click", '[name="mobilebutton_13"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        restservice1.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };
                    Apperyio.navigateTo('Screen1', {
                        reverse: false
                    });
                }
            },
        }, '[name="mobilebutton_13"]');
    };
    $(document).off("pagebeforeshow", "#Account").on("pagebeforeshow", "#Account", function(event, ui) {
        Apperyio.CurrentScreen = "Account";
        _.chain(Apperyio.mappings)
            .filter(function(m) {
                return m.homeScreen === Apperyio.CurrentScreen;
            })
            .each(Apperyio.UIHandler.hideTemplateComponents);
    });
    Account_onLoad();
};
$(document).off("pagecreate", "#Account").on("pagecreate", "#Account", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Account_js();
});